mapper.select:
SELECT DISTINCT user_id FROM sys_user_role WHERE role_id IN
#{roleId}
AND EXISTS ( SELECT 1 FROM sys_user u WHERE u.user_id = sys_user_role.user_id AND u.status = '0' AND u.del_flag = '0' )

SELECT role_id
FROM sys_user_role
WHERE user_id = #{userId}

SELECT COUNT(1) > 0
FROM sys_user_role
WHERE user_id = #{userId}
  AND role_id = #{roleId}
