mapper.update:
UPDATE sys_notification_user
SET is_read = '1',
    read_time = #{readTime},
    update_time = #{readTime}
WHERE user_id = #{userId}
  AND is_read = '0'

mapper.select:
SELECT *
FROM sys_notification_user
WHERE notification_id = #{notificationId}
  AND user_id = #{userId}
LIMIT 1

SELECT nu.*, n.notification_id as n_notification_id, n.template_id as n_template_id, n.type as n_type, n.title as n_title, n.content as n_content, n.source_type as n_source_type, n.source_id as n_source_id, n.priority as n_priority, n.expiration_date as n_expiration_date, n.actions as n_actions, n.attachments as n_attachments, n.business_data as n_business_data, n.is_merged as n_is_merged, n.merged_count as n_merged_count, n.status as n_status, n.create_time as n_create_time FROM sys_notification_user nu JOIN sys_notification n ON nu.notification_id = n.notification_id WHERE nu.user_id = #{query.userId}
AND nu.is_read = #{query.isRead}
AND nu.is_processed = #{query.isProcessed}
ORDER BY n.priority DESC, nu.create_time DESC LIMIT #{offset}, #{limit}

SELECT nu.*, n.notification_id as n_notification_id, n.template_id as n_template_id, n.type as n_type, n.title as n_title, n.content as n_content, n.source_type as n_source_type, n.source_id as n_source_id, n.priority as n_priority, n.expiration_date as n_expiration_date, n.actions as n_actions, n.attachments as n_attachments, n.business_data as n_business_data, n.is_merged as n_is_merged, n.merged_count as n_merged_count, n.status as n_status, n.create_time as n_create_time FROM sys_notification_user nu JOIN sys_notification n ON nu.notification_id = n.notification_id WHERE nu.user_id = #{query.userId} AND n.type = #{type}
AND nu.is_read = #{query.isRead}
AND nu.is_processed = #{query.isProcessed}
ORDER BY n.priority DESC, nu.create_time DESC LIMIT #{offset}, #{limit}

SELECT nu.*, n.notification_id as n_notification_id, n.template_id as n_template_id, n.type as n_type, n.title as n_title, n.content as n_content, n.source_type as n_source_type, n.source_id as n_source_id, n.priority as n_priority, n.expiration_date as n_expiration_date, n.actions as n_actions, n.attachments as n_attachments, n.business_data as n_business_data, n.is_merged as n_is_merged, n.merged_count as n_merged_count, n.status as n_status, n.create_time as n_create_time FROM sys_notification_user nu JOIN sys_notification n ON nu.notification_id = n.notification_id WHERE nu.user_id = #{query.userId} AND n.type = 'todo' AND n.status = '0'
AND nu.is_processed = #{query.isProcessed}
ORDER BY n.priority DESC, CASE WHEN n.expiration_date IS NOT NULL THEN n.expiration_date ELSE '9999-12-31 23:59:59' END ASC, nu.create_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(1)
FROM sys_notification_user
WHERE notification_id = #{notificationId}

SELECT COUNT(1)
FROM sys_notification_user
WHERE notification_id = #{notificationId}
  AND is_read = #{isRead}

SELECT COUNT(1)
FROM sys_notification_user
WHERE notification_id = #{notificationId}
  AND is_processed = #{isProcessed}

SELECT user_id
FROM sys_notification_user
WHERE notification_id = #{notificationId}
