mapper.update:
UPDATE sys_notification_user
SET is_read = '1',
    read_time = #{readTime},
    update_time = #{readTime}
WHERE user_id = #{userId}
  AND is_read = '0'

UPDATE sys_notification_user nu
SET nu.is_read = '1',
nu.read_time = #{readTime},
nu.update_time = #{readTime}
WHERE nu.user_id = #{userId}
AND nu.is_read = '0'
AND EXISTS (
SELECT 1 FROM sys_notification n
WHERE n.notification_id = nu.notification_id

    AND n.type = #{type}

AND n.status = '0'
)

mapper.select:
SELECT *
FROM sys_notification_user
WHERE notification_id = #{notificationId}
  AND user_id = #{userId}
LIMIT 1

SELECT nu.notification_id FROM sys_notification_user nu JOIN sys_notification n ON nu.notification_id = n.notification_id WHERE nu.user_id = #{userId}
AND n.type = #{type}
AND nu.is_read = #{isRead}
AND n.status = '0' ORDER BY n.priority, CASE WHEN n.priority = 'high' THEN 1 WHEN n.priority = 'medium' THEN 2 ELSE 3 END, n.create_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(1)
FROM sys_notification_user nu
JOIN sys_notification n ON nu.notification_id = n.notification_id
WHERE nu.user_id = #{userId}

    AND n.type = #{type}


    AND nu.is_read = #{isRead}

AND n.status = '0'
